global P
P  = 0.002 # A/V^(3/2)
Pm = 1000*P

def iP(vPK):
  if vPK > 0:
     return(P*vPK**1.5)
  else:
     return(0)

def DiP(VPK):
  if VPK > 0:
     return(1.5*P*math.sqrt(VPK))
  else:
     return(0)

